import React, {useState} from 'react';
import axios from 'axios';
import {API_BASE_URL} from '../../constants/apiConstants';
import { withRouter } from "react-router-dom";

function Home(props) {
    const [state , setState] = useState({
        data: null
        })

    const redirectToComplaint = () =>  {
        props.updateTitle('Complaint')
        props.history.push('/complaint');
    }

    const redirectToHome = () =>  {
       setState({data:{}})
       props.updateTitle('Home')
    }


    const RetreieveInfo = async () => {   
        const payload={
            "email": props.userLoggedIn
        }
        axios.post(API_BASE_URL+'getcomplaints', payload)
        .then(function (response) {
            if(response.data.code === 200){
                console.log(response.data.success)
                // props.showError(null)
                console.log(state)
                // return response.data.success

            }else{
                props.showError("Could not Get Complaint");
            }
        })
        .catch(function (error) {
            console.log(error);
        });

    }


    // const redirectToGetComplaint = () =>  {
    //     props.updateTitle('Get Complaint')
    //     props.history.push('/getcomplaints');
    // }

    const redirectToRegister = () => {
        props.history.push('/register'); 
        props.updateTitle('Register');
    }

    const page = <div className="card col-12 col-lg-4 login-card mt-2 hv-center">
    <button type="button" className="btn btn-primary btn-lg btn-block" onClick={() => redirectToHome()}>Back</button>
      <ul className="list-group li=st-group-flush">
        <li className="list-group-item text-left" >Title: </li>
        <li className="list-group-item text-left">Business Unit:</li>
        <li className="list-group-item text-left">Location: </li>
        <li className="list-group-item text-left">Complaint Description: </li>
        <li className="list-group-item text-left">Complaint Status: </li>
    </ul>
    </div>

    switch(true){
        case (state.data !== null):
            console.log("Here is the data:",state.data)
            return page
        default:
            return(
                <div className="mt-2">
                    <button type="button" className="btn btn-primary btn-lg btn-block" onClick={() => redirectToComplaint()}>Send a new complaint</button>
                    <button type="button" className="btn btn-primary btn-lg btn-block" onClick={()=>RetreieveInfo()}>Retrieve previous complaint</button> 

                </div>
            )
    }
    // return(
    //     <div className="mt-2">
    //         <button type="button" className="btn btn-primary btn-lg btn-block" onClick={() => redirectToComplaint()}>Send a new complaint</button>
    //         <button type="button" className="btn btn-primary btn-lg btn-block" onClick={()=> redirectToGetComplaint()}>Retrieve previous complaint</button> 

    //     </div>
    // )
}

export default withRouter(Home);
