import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {API_BASE_URL} from '../../constants/apiConstants';
import { withRouter } from "react-router-dom";


function RetrieveComplaint(props) {
    const [state , setState] = useState({
        data : {}
    })

    const RetreieveInfo = () => {
        const payload={
            "email": props.userLoggedIn
        }
        axios.post(API_BASE_URL+'getcomplaints', payload)
        .then(function (response) {
            if(response.data.code === 200){
                setState(response.data.success)
                props.showError(null)
            }else{
                props.showError("Could not Get Complaint");
            }
        })
        .catch(function (error) {
            console.log(error);
        });
    }

    const redirectToHome = () => {
        props.updateTitle('Home')
        props.history.push('/home');
    }

    useEffect(() => {
        RetreieveInfo()
    }, [])

    return(
        <div className="card col-12 col-lg-4 login-card mt-2 hv-center">
        <button type="button" className="btn btn-primary btn-lg btn-block" onClick={() => redirectToHome()}>Back</button>
          <ul class="list-group list-group-flush">
            <li class="list-group-item">Title: {state.data.title} </li>
            <li class="list-group-item">Business Unit: {state.data.businessUnit}</li>
            <li class="list-group-item">Location: {state.data.location}</li>
            <li class="list-group-item">Complaint Description: {state.data.description}</li>
            <li class="list-group-item">Complaint Status: {state.data.status}</li>
        </ul>
        </div>
    )
}

export default withRouter(RetrieveComplaint);
