import React, {useState} from 'react';
import axios from 'axios';
import {API_BASE_URL} from '../../constants/apiConstants';
import { withRouter } from "react-router-dom";


function Complaint(props) {
    const [state , setState] = useState({
        title : "",
        business_unit : "Dubai",
        location : "",
        complaint : "",
        status: null
        })

    const handleChange1 = (e) => {
        const {id, value} = e.target
        setState({
            [id]: value
        })
    }
    const handleChange = (event) => {
        const {name, value, type, checked} = event.target
        type === "checkbox" ? this.setState({ [name]: checked }) : this.setState({ [name]: value })
    }

    const handleSubmitClick = (e) => {
        e.preventDefault();
        const payload={
            "title":state.title,
            "Complaint":state.complaint,
            "status": null
        }
        axios.post(API_BASE_URL+'complaint', payload)
        .then(function (response) {
            if(response.data.code === 200){
                redirectToHome();
                props.showError(null)
            }
            else if(response.data.code === 204){
                props.showError("Could not Submit Complaint");
            }
            else{
                props.showError("Could not Submit Complaint");
            }
        })
        .catch(function (error) {
            console.log(error);
        });
    }
    const redirectToHome = () => {
        props.updateTitle('Home')
        props.history.push('/home');
    }

    return(
        <div className="card col-12 col-lg-4 login-card mt-2 hv-center">
            <form>
                <div className="form-group text-left">
                <label for="exampleFormControlInput1">Title</label>
                <input 
                    type="text"
                    className="form-control" 
                    value={state.title} 
                    name="title" 
                    placeholder="Title" 
                    onChange={handleChange} 
                />
                </div>
                <br />
                <div class="form-group text-left">
                    <label for="exampleFormControlSelect1">Business Unit</label>
                    <select class="form-control" name="business_unit" value={state.business_unit} onChange={handleChange}>
                        <option>--Select Business Unit--</option>
                        <option>Food</option>
                        <option>Electronics</option>
                        <option>Clothing</option>
                    </select>
                </div>
                <br />
                <div class="form-check text-left">
                    <input class="form-check-input" type="radio"  name="location" 
                    value="Dubai" checked={state.business_unit === "Dubai"} onChange={handleChange} />
                    <label class="form-check-label" for="exampleRadios">Dubai</label>
                </div>

                <div class="form-check text-left">
                    <input class="form-check-input" type="radio"  name="location" 
                    value="Abu Dhabi" checked={state.business_unit === "Abu Dhabi"} onChange ={handleChange}/>
                    <label class="form-check-label" for="exampleRadios">Abu Dhabi</label>
                </div>
                <br />
                <div className="form-group text-left">
                    <label for="exampleFormControlTextarea1">Complaint Description</label>
                    <textarea
                        className ="form-control"
                        value={state.complaint}
                        id="complaint" 
                        placeholder="Enter Complaint Here"
                        onChange={handleChange}
                    />
                </div>
                <div className="form-check">
                </div>
                <button 
                    type="submit" 
                    className="btn btn-primary"
                    onClick={handleSubmitClick}
                >Submit</button>

            </form>
        </div>
    )
}

export default withRouter(Complaint);
