import React, {useState} from 'react';
import axios from 'axios';
import {API_BASE_URL} from '../../constants/apiConstants';
import { withRouter } from "react-router-dom";


function Complaint(props) {
    const [state , setState] = useState({
        title : "",
        complaint : ""
        })

    const handleChange = (e) => {
        const {id, value} = e.target
        setState({
            [id]: value
        })
    }

    const handleSubmitClick = (e) => {
        e.preventDefault();
        const payload={
            "title":state.title,
            "Complaint":state.complaint,
        }
        axios.post(API_BASE_URL+'complaint', payload)
        .then(function (response) {
            if(response.data.code === 200){
                redirectToHome();
                props.showError(null)
            }
            else if(response.data.code === 204){
                props.showError("Could not Submit Complaint");
            }
            else{
                props.showError("Could not Submit Complaint");
            }
        })
        .catch(function (error) {
            console.log(error);
        });
    }
    const redirectToHome = () => {
        props.updateTitle('Home')
        props.history.push('/home');
    }

    return(
        <div className="card col-12 col-lg-4 login-card mt-2 hv-center">
            <form>
                <div className="form-group text-left">
                <label for="exampleFormControlInput1">Title</label>
                <input 
                    type="text"
                    className="form-control" 
                    value={state.title} 
                    id="title" 
                    placeholder="Title" 
                    onChange={handleChange} 
                />
                </div>
                <br />
                <div className="form-group text-left">
                <label for="exampleFormControlTextarea1">Complaint</label>
                <textarea
                    className ="form-control"
                    value={state.complaint}
                    id="complaint" 
                    placeholder="Enter Complaint Here"
                    onChange={handleChange}
                />
                </div>
                <div className="form-check">
                </div>
                <button 
                    type="submit" 
                    className="btn btn-primary"
                    onClick={handleSubmitClick}
                >Submit</button>

            </form>
        </div>
    )
}

export default withRouter(Complaint);
